public interface CorrelationStrategy {

  Object getCorrelationKey(Message<?> message);

}

public class PojoAggregator {

  public Long add(List<Long> results) {
    long total = 0l;
    for (long partialResult: results) {
      total += partialResult;
    }
    return total;
  }

}

